import './App.css';
import React, { useState } from 'react';
import Photos from './Components/Photos';
import Favourites from './Components/Favourites';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom';
import { FaSearch } from 'react-icons/fa';

function App() {
    const [query, setQuery] = useState('');
    const navigate = useNavigate();

    const handleSearch = (e) => {
        e.preventDefault();
        const newQuery = e.target[0].value;
        setQuery(newQuery);
        // Navigate to home route with search query
        navigate(`/photos?q=${newQuery}`);
    };

    // Define or import handleRemoveFavourite function if needed

    return (
        <BrowserRouter>
            <div>
                <nav className='navbar'>
                    <div className='navbar_logo'>Fotoflix</div>

                    <form action='' className='navbar_search-form' onSubmit={handleSearch}>
                        <input type='text' className='form-input' placeholder='search' />
                        <button type='submit' className='submit-btn'>
                            <FaSearch />
                        </button>
                    </form>

                    <div className='navbar_links'>
                        <Link to='/'>Home</Link>
                        <Link to='/favourites'>Favourites</Link>
                    </div>
                </nav>
                <Routes>
                    <Route path='/' element={<Photos searchQuery={query} />} />
                    {/* Make sure to pass correct props */}
                    <Route path='/favourites' element={<Favourites favouritePhotos={[]} handleRemoveFavourite={() => {}} />} />
                </Routes>
            </div>
        </BrowserRouter>
    );
}

export default App;
