import React, { useState, useEffect } from 'react';
import { FaHeart, FaDownload, FaShare, FaThumbsUp } from 'react-icons/fa';
import Lightbox from 'react-image-lightbox';

const Photos = ({ searchQuery, isFavorite, onFavoriteClick, ...photo }) => {
    const [loading, setLoading] = useState(false);
    const [photos, setPhotos] = useState([]);
    const [favoritePhotos, setFavoritePhotos] = useState([]);
    const [lightboxIndex, setLightboxIndex] = useState(0);
    const [isLightboxOpen, setIsLightboxOpen] = useState(false);
    const [page, setPage] = useState(1);

    useEffect(() => {
        const fetchImages = async () => {
            setLoading(true);
            const clientID = '?client_id=B3czRMQBgi-XvmLx097QA1mXAeu_qaYqhtuMyCOs2ak';
            const mainUrl = 'https://api.unsplash.com/photos/';
            let url = mainUrl + clientID;
            if (searchQuery) {
                url = `https://api.unsplash.com/search/photos/${clientID}&query=${searchQuery}`;
            }

            url += `&page=${page}`;

            try {
                const response = await fetch(url);
                const data = await response.json();

                setPhotos(data.results || data);
                setLoading(false);
            } catch (error) {
                setLoading(false);
                console.error('Error fetching images:', error);
            }
        };
        fetchImages();
    }, [searchQuery, page]);

    useEffect(() => {
        const handleScroll = () => {
            if (!loading && window.innerHeight + window.scrollY >= document.body.scrollHeight - 200) {
                setPage((prevPage) => prevPage + 1);
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [loading]);

    const handleFavouriteClick = (photoId) => {
        const existingIndex = favoritePhotos.findIndex((favPhoto) => favPhoto.id === photoId);

        if (existingIndex !== -1) {
            setFavoritePhotos((prevFavorites) =>
                prevFavourites.filter((favPhoto) => favPhoto.id !== photoId)
            );
        } else {
            const photoToAdd = photos.find((photo) => photo.id === photoId);
            setFavoritePhotos((prevFavorites) => [...prevFavorites, photoToAdd]);
        }
    };

    const handleShare = (photoUrl) => {
        const shareUrl = `https://api.whatsapp.com/send?text=${encodeURIComponent(
            `Checkout this awesome photo: ${photoUrl}`
        )}`;
        window.open(shareUrl, '_blank');
    };

    const handleDownload = (photoUrl, photoId) => {
        const link = document.createElement('a');
        link.href = photoUrl;
        link.download = `photo_${photoId}.jpg`;
        link.click();
    };

    const openLightbox = (index) => {
        setLightboxIndex(index);
        setIsLightboxOpen(true);
    };

    const closeLightbox = () => {
        setIsLightboxOpen(false);
    };

    return (
        <main>
            <section className='photos'>
                <div className='photos-center'>
                    {loading ? (
                        <p>Loading ...</p>
                    ) : (
                        photos.map((photo, index) => (
                            <article key={photo.id} className={`photo ${favoritePhotos.some((favPhoto) => favPhoto.id === photo.id) ? 'favorite-photo' : ''}`}>
                                <img src={photo.urls.regular} alt={photo.alt_description} onClick={() => openLightbox(index)} />
                                <div className='photo-info'>
                                    <div className='photo-header'>
                                        <h4>{photo.user.name}</h4>
                                        <button className={`favourite-btn ${favoritePhotos.some((favPhoto) => favPhoto.id === photo.id) ? 'active' : ''}`} onClick={() => handleFavouriteClick(photo.id)}>
                                            <FaHeart />
                                        </button>
                                    </div>
                                    <div className='photo-actions'>
                                        <p>
                                            <FaThumbsUp className='heart-icon' />
                                            {photo.likes}
                                        </p>
                                        <button className='share-btn' onClick={() => handleShare(photo.urls.regular)}>
                                            <FaShare />
                                        </button>

                                        <button className='download-btn' onClick={() => handleDownload(photo.urls.full, photo.id)}>
                                            <FaDownload />
                                        </button>
                                    </div>
                                    <a href={photo.user.portfolio_url}>
                                        <img src={photo.user.profile_image.medium} className='user-img' alt={photo.user.name} />
                                    </a>
                                </div>
                            </article>
                        ))
                    )}
                </div>
            </section>
            {isLightboxOpen && <Lightbox mainSrc={photos[lightboxIndex].urls.full} onCloseRequest={closeLightbox}></Lightbox>}
        </main>
    );
};

export default Photos;
